import React, { useState, useEffect } from 'react'
import MATRIX_FRAMES from './data/matrix'

const minDelay = 10
const minIncrement = 1

function Matrix(){
  const [ idx, setIdx ] = useState(0)
  const [ delay, setDelay ] = useState(500)
  const [ increment, setIncrement ] = useState(5)

  useEffect( () => {
    const interval = setInterval( () => {
      setIdx( storedIdx => {
        return (storedIdx + increment) % MATRIX_FRAMES.length
      })
    }, delay)
    return () => clearInterval(interval)
  }, [delay, increment])

  const updateDelay = event => {
    const delay = Number(event.target.value)

    setDelay(delay < minDelay ? minDelay : delay)
  }

  const updateIncrement = event => {
    const increment = Number(event.target.value)

    setIncrement(increment < minIncrement ? minIncrement : increment)
  }

  console.log('delay', delay, 'increment', increment)

  return (
    <div className="Matrix">
      <img src={ MATRIX_FRAMES[idx] } alt="matrix-animation"/>
      <div className="multiform">
        <div>
          Frame transition delay (seconds):
          <input type="number" onChange={ updateDelay }/>
        </div>
        <div>
          Frame increment:
          <input type="number" onChange={ updateIncrement }/>
        </div>
      </div>
    </div>
  )
}

export default Matrix
